// Normalizar colunas e adicionar source
const items = $input.all();
const processedItems = [];

// Mapear node names para sources
const sourceMap = {
  'Carregar ATIVOS': 'ativos',
  'Carregar DESLIGADOS': 'desligados',
  'Carregar AFASTAMENTOS': 'afastamentos',
  'Carregar FERIAS': 'ferias',
  'Carregar ADMISSAO': 'admissao',
  'Carregar ESTAGIO': 'estagio',
  'Carregar EXTERIOR': 'exterior',
  'Carregar Base Dias √öteis': 'diasUteis',
  'Carregar Base Sindicato Valor': 'sindicatoValor'
};

for (let item of items) {
  const data = item.json;
  const normalizedData = {};
  const sourceName = sourceMap[item.pairedItem?.item?.node] || 'unknown';

  // Normalizar nomes das colunas
  for (let key in data) {
    if (data.hasOwnProperty(key)) {
      const normalizedKey = key.trim().toUpperCase();
      normalizedData[normalizedKey] = data[key];
    }
  }

  processedItems.push({
    json: {
      ...normalizedData,
      _source: sourceName
    }
  });
}

return processedItems;

// Identificar matr√≠culas para exclus√£o
let matriculasExcluir = new Set();
const items = $input.all();

items.forEach(item => {
  const data = item.json;
  const source = data._source;

  try {
    switch(source) {
      case 'estagio':
        if (data.MATRICULA) matriculasExcluir.add(data.MATRICULA.toString());
        break;

      case 'exterior':
        if (data.CADASTRO) matriculasExcluir.add(data.CADASTRO.toString());
        break;

      case 'afastamentos':
        if (data.MATRICULA) matriculasExcluir.add(data.MATRICULA.toString());
        break;

      case 'ativos':
        const cargo = (data['TITULO DO CARGO'] || data['T√çTULO DO CARGO'] || '').toString().toUpperCase();
        if (cargo.includes('DIRETOR') || cargo.includes('APRENDIZ')) {
          if (data.MATRICULA) matriculasExcluir.add(data.MATRICULA.toString());
        }
        break;
    }
  } catch (error) {
    console.log(`Erro processando item do source ${source}:`, error.message);
  }
});

console.log(`Total de matr√≠culas para exclus√£o: ${matriculasExcluir.size}`);

return [{
  json: {
    matriculasExcluir: Array.from(matriculasExcluir),
    totalExclusoes: matriculasExcluir.size
  }
}];

// Filtrar colaboradores ativos eleg√≠veis
const allItems = $('Normalizar Colunas').all();
const exclusoesData = $input.first().json;

// Extrair apenas colaboradores ativos
const colaboradoresAtivos = allItems.filter(item => item.json._source === 'ativos');

// Filtrar excluindo as matr√≠culas identificadas
const colaboradoresElegiveis = colaboradoresAtivos.filter(item => {
  const matricula = item.json.MATRICULA ? item.json.MATRICULA.toString() : null;
  return matricula && !exclusoesData.matriculasExcluir.includes(matricula);
});

console.log(`Colaboradores ativos: ${colaboradoresAtivos.length}`);
console.log(`Colaboradores eleg√≠veis: ${colaboradoresElegiveis.length}`);
console.log(`Exclu√≠dos: ${colaboradoresAtivos.length - colaboradoresElegiveis.length}`);

return colaboradoresElegiveis.map(item => ({
  json: {
    ...item.json,
    _source: undefined // Remove source tag
  }
}));

// Merge com dados de f√©rias
const colaboradores = $input.all();
const feriasItems = $('Normalizar Colunas').all().filter(item => item.json._source === 'ferias');

const colaboradoresComFerias = colaboradores.map(colaboradorItem => {
  const colaborador = colaboradorItem.json;
  const matricula = colaborador.MATRICULA ? colaborador.MATRICULA.toString() : null;

  // Buscar informa√ß√µes de f√©rias
  const feriaInfo = feriasItems.find(item => {
    const feriaMatricula = item.json.MATRICULA ? item.json.MATRICULA.toString() : null;
    return feriaMatricula === matricula;
  });

  let diasFerias = 0;
  if (feriaInfo) {
    const diasFeriasRaw = feriaInfo.json['DIAS DE F√âRIAS'] || feriaInfo.json['DIAS DE FERIAS'] || feriaInfo.json['DIAS_DE_FERIAS'] || 0;
    diasFerias = parseInt(diasFeriasRaw) || 0;
  }

  return {
    json: {
      ...colaborador,
      DIAS_FERIAS: diasFerias
    }
  };
});

console.log(`Colaboradores processados com f√©rias: ${colaboradoresComFerias.length}`);

return colaboradoresComFerias;

// Merge com dados de desligamento
const colaboradores = $input.all();
const desligadosItems = $('Normalizar Colunas').all().filter(item => item.json._source === 'desligados');

const colaboradoresComDeslig = colaboradores.map(colaboradorItem => {
  const colaborador = colaboradorItem.json;
  const matricula = colaborador.MATRICULA ? colaborador.MATRICULA.toString() : null;

  // Buscar informa√ß√µes de desligamento
  const desligInfo = desligadosItems.find(item => {
    const desligMatricula = item.json.MATRICULA ? item.json.MATRICULA.toString().trim() : null;
    return desligMatricula === matricula;
  });

  let dataDeslig = null;
  let desligOk = false;

  if (desligInfo) {
    // Tentar diferentes formatos de data
    const dataRaw = desligInfo.json['DATA DEMISS√ÉO'] || desligInfo.json['DATA DEMISSAO'] || desligInfo.json['DATA_DEMISSAO'];
    if (dataRaw) {
      dataDeslig = new Date(dataRaw);
      if (isNaN(dataDeslig.getTime())) {
        dataDeslig = null;
      }
    }

    const comunicado = (desligInfo.json['COMUNICADO DE DESLIGAMENTO'] || desligInfo.json['COMUNICADO_DE_DESLIGAMENTO'] || '').toString().toUpperCase().trim();
    desligOk = comunicado === 'OK';
  }

  return {
    json: {
      ...colaborador,
      DATA_DEMISSAO: dataDeslig ? dataDeslig.toISOString() : null,
      DESLIGADO_OK: desligOk
    }
  };
});

console.log(`Colaboradores processados com desligamento: ${colaboradoresComDeslig.length}`);

return colaboradoresComDeslig;

// Associar estado, sindicato e valores
const colaboradores = $input.all();
const diasUteisItems = $('Normalizar Colunas').all().filter(item => item.json._source === 'diasUteis');
const valoresSindItems = $('Normalizar Colunas').all().filter(item => item.json._source === 'sindicatoValor');

// Mapeamento sindicato -> estado
const sindToEstado = {
  'SINDPPD RS - SINDICATO DOS TRAB. EM PROC. DE DADOS RIO GRANDE DO SUL': 'RIO GRANDE DO SUL',
  'SINDPD RJ - SINDICATO PROFISSIONAIS DE PROC DADOS DO RIO DE JANEIRO': 'RIO DE JANEIRO',
  'SINDPD SP - SIND.TRAB.EM PROC DADOS E EMPR.EMPRESAS PROC DADOS ESTADO DE SP.': 'S√ÉO PAULO',
  'SITEPD PR - SIND DOS TRAB EM EMPR PRIVADAS DE PROC DE DADOS DE CURITIBA E REGIAO METROPOLITANA': 'PARAN√Å'
};

const colaboradoresEnriquecidos = colaboradores.map(colaboradorItem => {
  const colaborador = colaboradorItem.json;
  const sindicato = colaborador.SINDICATO || '';

  // Determinar estado
  const estado = sindToEstado[sindicato] || 'DESCONHECIDO';

  // Buscar dias √∫teis para o sindicato
  const diasUteisInfo = diasUteisItems.find(item => 
    item.json.SINDICATO === sindicato
  );
  const diasUteisCount = diasUteisInfo ? parseInt(diasUteisInfo.json.DIAS_UTEIS || diasUteisInfo.json['DIAS_√öTEIS'] || 0) : 0;

  // Buscar valor di√°rio pelo estado
  const valorInfo = valoresSindItems.find(item => {
    const estadoItem = (item.json.ESTADO || '').toUpperCase().trim();
    return estadoItem === estado.toUpperCase().trim();
  });
  const valorDiario = valorInfo ? parseFloat(valorInfo.json.VALOR || 0) : 0;

  return {
    json: {
      ...colaborador,
      ESTADO: estado,
      DIAS_UTEIS_SINDICATO: diasUteisCount,
      VALOR_DIARIO_VR: valorDiario
    }
  };
});

console.log(`Colaboradores enriquecidos: ${colaboradoresEnriquecidos.length}`);

return colaboradoresEnriquecidos;

// Calcular dias de VR baseado nas regras de neg√≥cio
function calcularDias(colaborador) {
  const diasUteis = parseInt(colaborador.DIAS_UTEIS_SINDICATO) || 0;
  const diasFerias = parseInt(colaborador.DIAS_FERIAS) || 0;
  const dataDesligStr = colaborador.DATA_DEMISSAO;
  const desligOk = colaborador.DESLIGADO_OK;

  // Se tem data de desligamento
  if (dataDesligStr) {
    const dataDeslig = new Date(dataDesligStr);

    // Verificar se desligou em maio/2025
    if (dataDeslig.getMonth() === 4 && dataDeslig.getFullYear() === 2025) {
      // Regra: at√© dia 15 com OK = n√£o paga
      if (desligOk && dataDeslig.getDate() <= 15) {
        return 0;
      }
      // Regra: ap√≥s dia 15 com OK = proporcional
      else if (desligOk && dataDeslig.getDate() > 15) {
        return dataDeslig.getDate();
      }
    }
    // Se n√£o tem OK ou desligou em outro per√≠odo, paga integral
    return diasUteis;
  }

  // Colaborador ativo: dias √∫teis menos f√©rias (m√≠nimo 0)
  return Math.max(0, diasUteis - diasFerias);
}

const colaboradores = $input.all();

const colaboradoresComDias = colaboradores.map(colaboradorItem => {
  const colaborador = colaboradorItem.json;
  const diasVR = calcularDias(colaborador);

  return {
    json: {
      ...colaborador,
      DIAS_VR: diasVR
    }
  };
});

// Log para auditoria
const totalDiasVR = colaboradoresComDias.reduce((sum, item) => sum + item.json.DIAS_VR, 0);
console.log(`Total de dias VR calculados: ${totalDiasVR}`);

return colaboradoresComDias;

// Calcular valores finais de VR
const colaboradores = $input.all();

const colaboradoresComValores = colaboradores.map(colaboradorItem => {
  const colaborador = colaboradorItem.json;

  const diasVR = colaborador.DIAS_VR || 0;
  const valorDiario = colaborador.VALOR_DIARIO_VR || 0;

  // C√°lculos
  const vrTotal = diasVR * valorDiario;
  const vrEmpresa = vrTotal * 0.8;  // 80% empresa
  const vrColaborador = vrTotal * 0.2; // 20% colaborador

  return {
