      VR_TOTAL: parseFloat(vrTotal.toFixed(2)),
      VR_EMPRESA: parseFloat(vrEmpresa.toFixed(2)),
      VR_COLABORADOR: parseFloat(vrColaborador.toFixed(2))
    }
