const totalEmpresa = colaboradoresComValores.reduce((sum, item) => sum + item.json.VR_EMPRESA, 0);
const totalColaborador = colaboradoresComValores.reduce((sum, item) => sum + item.json.VR_COLABORADOR, 0);

console.log(`Total VR Geral: R$ ${totalVRGeral.toFixed(2)}`);
console.log(`Total Empresa (80%): R$ ${totalEmpresa.toFixed(2)}`);
console.log(`Total Colaborador (20%): R$ ${totalColaborador.toFixed(2)}`);

return colaboradoresComValores;

